
public class Ejercicio_1App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*- Declara dos variables numéricas (con el valor que desees), muestra por consola la suma,
		resta, multiplicación, división y módulo (resto de la división).*/
		int a = 23, b = 7;
		
		System.out.println("La suma de A y B es: " + (a + b));
		System.out.println("La resta de A y B es: " + (a - b));
		System.out.println("La multiplicación de A y B es: " + (a * b));
		System.out.println("La división de A y B es: " + (a / b));
		System.out.println("La módulo de A y B es: " + (a % b));
	}

}
